library(data.table)

#set working directory
setwd('/Users/analutzky/Desktop/')


############################ GETTING STARTED ############################

# read French file of Parcoursup 2020
Table_parcoursup_2020=fread('/Users/analutzky/Desktop/fr-esr-parcoursup.csv')

# showing it excel-like
View(Table_parcoursup_2020)
# showing column names
colnames(Table_parcoursup_2020)
# put them into a variable
var.names=colnames(Table_parcoursup_2020)
### getting rid of space and special characters in columns names 
colnames(Table_parcoursup_2020)=make.names(var.names)
# showing column names
colnames(Table_parcoursup_2020)

Table_parcoursup_2020$Concours.communs.et.banques.d.épreuves
Table_parcoursup_2020$Établissement
Filieres=Table_parcoursup_2020$Filière.de.formation.très.agrégée
unique(Filieres)
