############################ PASS ############################

Table_PASS=Table_parcoursup_2020[Filière.de.formation.très.agrégée=="PASS",]
Effectifs_PASS=Table_PASS[, .(Filiere=Filière.de.formation.très.agrégée, Etab=Établissement, candidates=sum(Dont.effectif.des.candidates.pour.une.formation), candidats_PP=sum(Effectif.total.des.candidats.en.phase.principale), candidats_PC=sum(Effectif.total.des.candidats.en.phase.complémentaire), admis_PP=sum(Effectif.des.admis.en.phase.principale), admis_PC=sum(Effectif.des.admis.en.phase.complémentaire), admises=sum(Dont.effectif.des.candidates.admises), admis_neo_B=sum(Effectif.des.admis.néo.bacheliers), admis_neo_B_G=sum(Effectif.des.admis.néo.bacheliers.généraux), admis_neo_B_techno=sum(Effectif.des.admis.néo.bacheliers.technologiques), admis_neo_B_pro=sum(Effectif.des.admis.néo.bacheliers.professionnels), admis_neo_B_TB=sum(Dont.effectif.des.admis.néo.bacheliers.avec.mention.Très.Bien.au.bac)),by=Établissement]
# (capacite=sum(Capacité.de.l.établissement.par.formation), candidats=sum(Effectif.total.des.candidats.pour.une.formation)
# Error in list(sum(Capacité.de.l.établissement.par.formation), sum(Effectif.total.des.candidats.pour.une.formation),  : 
#   l'argument 12 est vide

Effectifs_PASS=Effectifs_PASS[, part_candidates:=candidates/(candidats_PC+candidats_PP)*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, part_admises:=admises/(admis_PP+admis_PC)*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, selectivite:=(admis_PP+admis_PC)/(candidats_PC+candidats_PP)*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, part_admis_neo_B:=admis_neo_B/(admis_PP+admis_PC)*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, part_admis_neo_B_G_parmi_neoB:=admis_neo_B_G/admis_neo_B*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, part_admis_neo_B_techno_parmi_neoB:=admis_neo_B_techno/admis_neo_B*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, part_admis_neo_B_pro_parmi_neoB:=admis_neo_B_pro/admis_neo_B*100,by=Établissement]
Effectifs_PASS=Effectifs_PASS[, part_admis_neo_B_TB_parmi_neoB:=admis_neo_B_TB/admis_neo_B*100,by=Établissement]

############################ LAS ############################

Table_LAS=Table_parcoursup_2020[Filière.de.formation.très.agrégée=="Licence_Las",]
Effectifs_LAS=Table_LAS[, .(Filiere=Filière.de.formation.très.agrégée, Etab=Établissement, candidates=sum(Dont.effectif.des.candidates.pour.une.formation), candidats_PP=sum(Effectif.total.des.candidats.en.phase.principale), candidats_PC=sum(Effectif.total.des.candidats.en.phase.complémentaire), admis_PP=sum(Effectif.des.admis.en.phase.principale), admis_PC=sum(Effectif.des.admis.en.phase.complémentaire), admises=sum(Dont.effectif.des.candidates.admises), admis_neo_B=sum(Effectif.des.admis.néo.bacheliers), admis_neo_B_G=sum(Effectif.des.admis.néo.bacheliers.généraux), admis_neo_B_techno=sum(Effectif.des.admis.néo.bacheliers.technologiques), admis_neo_B_pro=sum(Effectif.des.admis.néo.bacheliers.professionnels), admis_neo_B_TB=sum(Dont.effectif.des.admis.néo.bacheliers.avec.mention.Très.Bien.au.bac)),by=Filière.de.formation.détaillée]

Effectifs_LAS=Effectifs_LAS[, part_candidates:=candidates/(candidats_PC+candidats_PP)*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, part_admises:=admises/(admis_PP+admis_PC)*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, selectivite:=(admis_PP+admis_PC)/(candidats_PC+candidats_PP)*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, part_admis_neo_B:=admis_neo_B/(admis_PP+admis_PC)*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, part_admis_neo_B_G_parmi_neoB:=admis_neo_B_G/admis_neo_B*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, part_admis_neo_B_techno_parmi_neoB:=admis_neo_B_techno/admis_neo_B*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, part_admis_neo_B_pro_parmi_neoB:=admis_neo_B_pro/admis_neo_B*100,by=Filière.de.formation.détaillée]
Effectifs_LAS=Effectifs_LAS[, part_admis_neo_B_TB_parmi_neoB:=admis_neo_B_TB/admis_neo_B*100,by=Filière.de.formation.détaillée]

############################ PASS ET LAS ############################
Effectifs_LAS_et_PASS=rbind(Effectifs_PASS, Effectifs_LAS)

